"use strict";

var pregunta1 = document.getElementById('pregunta1');
pregunta1.addEventListener('click', function () {
  // console.log('hola')
  var parrafos = document.getElementById('info-desplegable1');

  if (parrafos.style.display == "none") {
    parrafos.style.display = "block";
  } else {
    parrafos.style.display = "none";
  }
});
